export var uuidv4 = function () {
    return ([1e7] + -1e3 + -4e3 + -8e3 + -1e11).replace(/[018]/g, function (c) {
        return (c ^ (crypto.getRandomValues(new Uint8Array(1))[0] & (15 >> (c / 4)))).toString(16);
    });
};
export var generateApplicationId = function () {
    var now = new Date();
    return "".concat(now.toISOString(), "-").concat(uuidv4());
};
export var validateEmail = function (email) {
    return email.match(/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/);
};
// To extend classNames
export var extend = function (className, extendedClassName) {
    return extendedClassName ? "".concat(className, " ").concat(className, "-").concat(extendedClassName) : className;
};
